import React from "react";
import { Link } from "react-router-dom";
import "../Highlights.css";

const Article = ({
  section,
  published_date,
  title,
  byline,
  multimedia,
  abstract,
  url,
}) => {
  const formatDate = (date) => {
    const options = {
      year: "numeric",
      month: "long",
      day: "numeric",
    };
    return new Date(date).toLocaleDateString("en-EN", options);
  };

  return (
    <div className="article-container">
      {/* Sezione */}
      <div>
        <p className="section-name">{section}</p>
      </div>

      {/* Immagine */}
      {multimedia && (
        <div className="article-image">
          <img
            src={multimedia[1] ? multimedia[1].url : "default-image.jpg"}
            alt={title}
            className="image"
          />
        </div>
      )}

      {/* Testo e dettagli */}
      <div className="article-content">
        {/* Titolo */}
        <h2 className="article-title">
          <Link to={url} target="_blank" rel="noreferrer">
            {title}
          </Link>
        </h2>

        {/* Abstract */}
        <p className="article-abstract">{abstract}</p>

        {/* Dati: Byline e Data di pubblicazione */}
        <div className="article-meta">
          {byline && <p className="article-byline">{byline}</p>}
          <p className="article-date">{formatDate(published_date)}</p>
        </div>
      </div>
    </div>
  );
};

export default Highlights;
