import React from "react";
import { useGlobalContext } from "../context.js";
import { Link } from "react-router-dom";


  const ArticleImageRight = ({
    title,
    byline,
    multimedia,
    abstract,
    url,
    author,
  }) => {
    const { formatSection } = useGlobalContext();

  return (
    <div className="max-w-5xl mx-auto p-6">
      <article className="bg-white overflow-hidden transition-transform duration-300 hover:shadow-xl">
        <div className="flex flex-col md:flex-row p-6 gap-8">
          <div className="flex-1 space-y-4">
            <h2 
              className={`text-2xl font-bold text-gray-800 transition-colors duration-300}`}
            ><Link to={url} target="_blank" rel="noreferrer">
              {title}</Link>
            </h2>
            <p className="text-gray-600 leading-relaxed">
                {abstract}
            </p>
          </div>
          
          <div className="md:w-[540px] space-y-4">
            <div className="relative group overflow-hidden">
              <img
                src={multimedia[0].url}
                className="w-full h-[250px] transition-transform duration-300 group-hover:scale-105"
              />
              <div className="absolute inset-0 bg-gradient-to-t from-black/50 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
            </div>
            
            <div className="flex items-right space-x-3 text-gray-600">
              <span className="font-small">{byline}</span>
            </div>
          </div>
        </div>
      </article>
    </div>
  );
};

export default ArticleImageRight;
