import React, { useState } from "react";
import "../ArticleSlideshow.css"

const ArticleSlideshow = ({ articles }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const nextArticle = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === articles.length - 1 ? 0 : prevIndex + 1
    );
  };

  const prevArticle = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? articles.length - 1 : prevIndex - 1
    );
  };

  return (
    <div className="slideshow-container">
      <div className="slideshow">
        {articles.slice(currentIndex, currentIndex + 3).map((article, index) => (
          <div key={index} className="slideshow-item">
            <img
              src={article.multimedia[0]?.url || ""}
              alt={article.title}
              className="slideshow-image"
            />
            <h2 className="slideshow-title">{article.title}</h2>
          </div>
        ))}
      </div>
      <div className="slideshow-controls">
        <button onClick={prevArticle} className="slideshow-prev">
          &#10094;
        </button>
        <button onClick={nextArticle} className="slideshow-next">
          &#10095;
        </button>
      </div>
    </div>
  );
};

export default ArticleSlideshow;
