import React from "react";
import { Link } from "react-router-dom";

const ArticlesProva= ({ articles }) => {
  const articlesLoad = articles?.results || [];

  if (articlesLoad.length === 0) {
    return null;
  }

  return (
    <div className="article">
      <div className="text-content">
        <Link to={articlesLoad[0].url} className="article-title">
          <h2>{articlesLoad[0].title}</h2>
        </Link>
        <p>{articlesLoad[0].abstract}</p>
        <div className="divider"></div>

        {articlesLoad[1] && (
          <Link to={articlesLoad[1].url} className="article-title">
            <h2 className="article-title-live">{articlesLoad[1].title}</h2>
          </Link>
        )}
      </div>

      <img
        src={articlesLoad[1]?.multimedia?.[1]?.url || ""}
        alt={articlesLoad[0].title}
      />
    </div>
  );
};

export default ArticlesProva
