import React, { useState, useEffect } from "react";
import Highlights from "./Highlights";  // Assicurati che il componente Highlights sia nel giusto percorso
import "../ArticleSlideshow.css";

const Homepage = () => {
  const [articlesLoad, setArticlesLoad] = useState([]);
  const [filteredArticles, setFilteredArticles] = useState([]);
  const sectionFilter = "Opinion";  // Qui inserisci la sezione che vuoi filtrare, es. 'Tecnologia'

  // Effettua il fetch degli articoli
  useEffect(() => {
    const fetchArticles = async () => {
      const response = await fetch("https://api.nytimes.com/svc/topstories/v2/home.json?api-key=30mS33uUBWOVAYffuOfb9rpPZhDzpDjS");  // Modifica con il tuo endpoint API
      const data = await response.json();

      setArticlesLoad(data.articles);  // Salva tutti gli articoli

      // Filtra gli articoli per sezione
      const filtered = data.articles.filter(article => article.section === sectionFilter);
      setFilteredArticles(filtered);  // Salva solo gli articoli della sezione desiderata
    };

    fetchArticles();
  }, []);

  return (
    <div className="homepage">
      {/* Colonna sinistra (con articoli filtrati per la sezione) */}
      <div className="left-column">
        {filteredArticles.map((article, index) => (
          <div key={index} className="article">
            <img src={article.multimedia[0]?.url || ""} alt={article.title} />
            <h2>{article.title}</h2>
            <p>{article.abstract}</p>
          </div>
        ))}
      </div>

      {/* Colonna destra con Highlights (sezione filtrata) */}
      <div className="right-column">
        <Highlights articles={filteredArticles} />
      </div>
    </div>
  );
};

export default Homepage;
