import React, { useState, useEffect } from "react";
import "./Highlights.css";

const Highlights = ({ apiEndpoint }) => {
  const [articles, setArticles] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [loading, setLoading] = useState(true);

  // Effettua il fetch dei dati
  useEffect(() => {
    const fetchArticles = async () => {
      try {
        const response = await fetch(apiEndpoint);
        if (!response.ok) {
          throw new Error("Errore durante il caricamento degli articoli");
        }
        const data = await response.json();
        setArticles(data.articles || []); // Supponiamo che l'API restituisca un oggetto con `articles`
      } catch (error) {
        console.error(error);
      } finally {
        setLoading(false);
      }
    };

    fetchArticles();
  }, [apiEndpoint]);

  // Controllo per navigare
  const handlePrevious = () => {
    setCurrentIndex((prevIndex) => (prevIndex > 0 ? prevIndex - 1 : prevIndex));
  };

  const handleNext = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex < articles.length - 2 ? prevIndex + 1 : prevIndex
    );
  };

  if (loading) {
    return <div>Caricamento in corso...</div>;
  }

  if (articles.length === 0) {
    return <div>Nessun articolo disponibile</div>;
  }

  return (
    <div className="highlights-container">
      <div
        className="highlights-grid"
        style={{ transform: `translateX(-${currentIndex * 50}%)` }}
      >
        {articles.map((article, index) => (
          <div className="highlight-card" key={index}>
            <img
              src={article.image || "default-image.jpg"} // Immagine di default se manca
              alt={article.title || "Senza titolo"}
              className="highlight-image"
            />
            <div className="highlight-title">{article.title || "Senza titolo"}</div>
          </div>
        ))}
      </div>
      <div className="highlights-controls">
        <button className="highlight-arrow left" onClick={handlePrevious}>
          ←
        </button>
        <button className="highlight-arrow right" onClick={handleNext}>
          →
        </button>
      </div>
    </div>
  );
};

export default Highlights;
