import React, { useState } from "react";
import "../RightSlideshow.css";


const SlideshowNoimage = ({ articles }) => {
    const [currentIndex, setCurrentIndex] = useState(0);
    const maxArticles = 4; // Numero massimo di articoli da scorrere
    const visibleArticles = 2; // Numero di articoli visibili contemporaneamente

    // Verifica che gli articoli esistano e non siano vuoti
    if (!articles || articles.length === 0) {
        return <div>No articles available </div>;
    }

    // Filtra solo i primi `maxArticles` articoli
    const filteredArticles = articles.slice(0, maxArticles);

    const nextArticle = () => {
        setCurrentIndex((prevIndex) =>
            (prevIndex + visibleArticles) % filteredArticles.length
        );
    };

    const prevArticle = () => {
        setCurrentIndex((prevIndex) =>
            (prevIndex - visibleArticles + filteredArticles.length) % filteredArticles.length
        );
    };

    return (
        <div className= "slideshow-container" >
        <div className="slideshow" >
        {
            filteredArticles
          .slice(currentIndex, currentIndex + visibleArticles)
                .map((article, index) => (
                    <div key= { index } className = "slideshow-item-noimage" >
                    
                    <h2 className="slideshow-title" > { article.title } </h2>
                < h2 className = "slideshow-byline" > { article.byline } </h2>
                </div>
                ))
        }
            </div>
            < div className = "slideshow-controls" >
                <button onClick={ prevArticle } className = "slideshow-prev" >
          &#10094;
    </button>
        < button onClick = { nextArticle } className = "slideshow-next" >
          &#10095;
    </button>
        </div>
        </div>
  );
};

export default SlideshowNoimage;
