const Article = ({
  section,
  published_date,
  title,
  byline,
  multimedia,
  abstract,
  url
}) => {
  // Funzione per formattare la sezione
  const formatSection = (section) => {
    if (!section || section.trim() === "") return "No Section";
    return section.charAt(0).toUpperCase() + section.slice(1);
  };

  // Funzione per formattare la data
  const formatDate = (date) => {
    if (!date) return "Unknown Date";
    const options = { year: "numeric", month: "long", day: "numeric" };
    return new Date(date).toLocaleDateString("en-US", options);
  };

  return (
    <div className="container flex flex-col justify-center items-center w-full p-0 m-0 border-b-2 border-gray-300">
      {/* Sezione */}
      <p className="section-name text-sm font-bold mt-2 bg-white rounded text-black p-1 capitalize">
        {formatSection(section)}
      </p>

      {/* Immagine */}
      {multimedia && multimedia[0] && (
        <div className="mt-0">
          <img
            className="w-full h-auto shadow-md"
            src={multimedia[0].url}
            alt={title}
          />
        </div>
      )}

      {/* Titolo */}
      <h2 className="font-extrabold text-lg w-full mx-auto mt-4 text-left">
        <Link to={url} target="_blank" rel="noreferrer">
          {title}
        </Link>
      </h2>

      {/* Abstract */}
      {abstract && (
        <div className="mt-4 text-justify justify-left w-full mx-auto pb-[10px]">
          <p className="text-sm">{abstract}</p>
        </div>
      )}

      {/* Info aggiuntive (byline e data di pubblicazione) */}
      <div className="mt-2 flex flex-row justify-between items-center">
        {/* Byline */}
        {byline && <p className="font-bold text-xs w-[50%]">{byline}</p>}
        {/* Data di pubblicazione */}
        <p className="font-bold mr-2 text-xs">{formatDate(published_date)}</p>
      </div>
    </div>
  );
};

export default Article;
