import React, { useState } from "react";
import { FaFacebook, FaTwitter, FaInstagram, FaLinkedin } from "react-icons/fa";
import { MdEmail, MdPhone, MdLocationOn } from "react-icons/md";
import { FaChevronDown, FaChevronUp, FaUser, FaBox, FaGift, FaTruck } from "react-icons/fa";

const Footer = () => {
  const [expandedSections, setExpandedSections] = useState({});

  const footerColumns = [
    {
      title: "News",
      links: [
        "Homepage", "U.S.", "World", "Politics", "NewYork", "Education",
        "Sports", "Business", "Tech", "Science", "Weather", "The Great Read",
        "Obituaries", "Headway", "Visual Investigation", "The Magazine"
      ]
    },
    {
      title: "Arts",
      links:  [
        "Book Reviews", "Best Seller Book List", "Dance", "Movies", "Music",
        "Pop Culture", "Television", "Theater", "Visual Arts"
      ]
    },
    {
      title: "Lifestyle",
      links:  [
        "Health", "Well", "Food", "Restaurant Reviews", "Love", "Travel",
        "Style", "Fashion", "Real Estate", "T Magazine"
      ]
    },
    {
      title: "Opinion",
      links: [
        "Today's Opinion", "Columnists", "Editorials", "Guest Essay", "Op-Docs",
        "Letters", "Sunday Opinion", "Opinion Video", "Opinion Audio"
      ]
    },
    {
      title: "More",
      links: [
        "Audio", "Games", "Cooking", "Wirecutter", "The Athletic", "Jobs", "Video",
        "Graphics", "Trending", "Live Events", "Corrections", "Reader Center",
        "Times Machine", "The Learning Network", "School of the NYT", "inEducation"
      ]
    },
    {
      title: "Account",
      links: [
        { label: "Subscribe", icon: <FaUser /> }, // Aggiungi l'icona accanto al testo
        { label: "Manage your account", icon: <FaBox /> },
        { label: "Home Delivery", icon: <FaTruck /> },
        { label: "Gift Subscriptions", icon: <FaGift /> }
      ]
    }
  ];

  const toggleSection = (title) => {
    setExpandedSections((prev) => ({
      ...prev,
      [title]: !prev[title],
    }));
  };

  return (
    <footer className="bg-white text-gray-900 py-12 px-4 sm:px-12 lg:px-[75px] shadow-lg">
      <div className="max-w-7xl mx-auto border-t-2 border-gray-300">
        <div className="mb-4 flex justify-start">
          <img src='./Title.png' alt="Company Logo" className="h-12 w-auto" />
        </div>
        <div className="grid grid-cols-1 lg:grid-cols-6 gap-8">
          {footerColumns.map((column) => (
            <div
              key={column.title}
              className="space-y-4"
              role="region"
              aria-label={`${column.title} section`}
            >
              {/* Titolo della sezione, cliccabile per espandere i link su mobile e tablet */}
              <div className="flex justify-between items-center cursor-pointer lg:cursor-auto" onClick={() => toggleSection(column.title)}>
                <h2 className="text-[13px] md:text-xs font-bold tracking-wider uppercase border-b border-gray-100 pb-2 transition-colors duration-300 hover:text-blue-600 w-full md:w-auto">
                  {column.title}
                </h2>
                {/* Icona che mostra espansione o chiusura solo sotto i 1025px */}
                <span className="lg:hidden">
                  {expandedSections[column.title] ? <FaChevronUp /> : <FaChevronDown />}
                </span>
              </div>

              {/* Links: visibili sopra i 1025px */}
              <ul className="hidden lg:block space-y-2">
                {column.links.map((link, linkIndex) => (
                  <li key={linkIndex} className="flex items-center space-x-2">
                    {/* Renderizzo l'icona e il testo separatamente */}
                    {link.icon && <span>{link.icon}</span>}
                    <a
                      href="#"
                      className="text-[12px] text-black hover:text-gray-800 transition-colors duration-300 block py-1 focus:outline-none focus:ring-2 focus:ring-blue-300 focus:ring-offset-2 focus:ring-offset-white rounded leading-[10px]"
                      aria-label={link.label || link}
                    >
                      {link.label || link}
                    </a>
                  </li>
                ))}
              </ul>

              {/* Links: visibili tra 1025px e 750px se la sezione è espansa */}
              {expandedSections[column.title] && (
                <ul className="lg:hidden space-y-2">
                  {column.links.map((link, linkIndex) => (
                    <li key={linkIndex} className="flex items-center space-x-2">
                      {/* Renderizzo l'icona e il testo separatamente */}
                      {link.icon && <span>{link.icon}</span>}
                      <a
                        href="#"
                        className="text-[12px] text-black hover:text-gray-800 transition-colors duration-300 block py-1 focus:outline-none focus:ring-2 focus:ring-blue-300 focus:ring-offset-2 focus:ring-offset-white rounded"
                        aria-label={link.label || link}
                      >
                        {link.label || link}
                      </a>
                    </li>
                  ))}
                </ul>
              )}
            </div>
          ))}
        </div>

        {/* Sezione Contatti */}
        <div className="mt-12 pt-8 border-t border-gray-100">
          <div className="flex flex-col md:flex-row justify-between items-center space-y-4 md:space-y-0">
            <div className="flex space-x-6">
              <a href="#" className="text-gray-400 hover:text-gray-800 transition-colors duration-300" aria-label="Facebook">
                <FaFacebook className="text-2xl" />
              </a>
              <a href="#" className="text-gray-400 hover:text-gray-800 transition-colors duration-300" aria-label="Twitter">
                <FaTwitter className="text-2xl" />
              </a>
              <a href="#" className="text-gray-400 hover:text-gray-800 transition-colors duration-300" aria-label="Instagram">
                <FaInstagram className="text-2xl" />
              </a>
              <a href="#" className="text-gray-400 hover:text-gray-800 transition-colors duration-300" aria-label="LinkedIn">
                <FaLinkedin className="text-2xl" />
              </a>
            </div>

            <div className="flex flex-col md:flex-row space-y-4 md:space-y-0 md:space-x-8 text-gray-500">
              <div className="flex items-center">
                <MdEmail className="mr-2" />
                <span>contact@example.com</span>
              </div>
              <div className="flex items-center">
                <MdPhone className="mr-2" />
                <span>+1 (555) 123-4567</span>
              </div>
              <div className="flex items-center">
                <MdLocationOn className="mr-2" />
                <span>123 Street, City, Country</span>
              </div>
            </div>
          </div>

          <div className="mt-8 text-center text-gray-500 text-sm">
            <p>© {new Date().getFullYear()} Your Company Name. All rights reserved.</p>
          </div>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
