import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import axios from "axios"; // se usi axios, altrimenti puoi usare fetch()

const ArticleGrid = ({ content }) => {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchArticles = async () => {
      try {
        const response = await axios.get(
          `https://api.nytimes.com/svc/search/v2/articlesearch.json?q=${content}&api-key=30mS33uUBWOVAYffuOfb9rpPZhDzpDjS`
        );
        setArticles(response.data.response.docs); // Salva i dati degli articoli
        setLoading(false);
      } catch (err) {
        setError("Failed to fetch articles");
        setLoading(false);
      }
    };

    fetchArticles();
  }, [content]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="flex justify-center items-center min-h-screen bg-gradient-to-r from-blue-50 to-indigo-50 p-8">
      <section className="max-w-[840px] mx-auto">
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-4">
          {articles.map((article) => (
            <motion.article
              key={article._id}
              className="bg-white rounded-xl overflow-hidden shadow-lg hover:shadow-xl transition-shadow duration-300 w-full"
              whileHover={{ scale: 1.03 }}
              transition={{ duration: 0.2 }}
            >
              <div className="relative overflow-hidden aspect-[4/3]">
                <motion.img
                  src={article.multimedia && article.multimedia[0] ? `https://static01.nyt.com/${article.multimedia[0].url}` : "https://via.placeholder.com/300"}
                  alt={article.headline.main || "No title"}
                  className="w-full h-full object-cover transition-transform duration-300 hover:scale-110"
                  loading="lazy"
                />
              </div>
              <div className="p-4">
                <h2 className="text-lg font-semibold text-gray-800 hover:text-indigo-600 transition-colors duration-300">
                  {article.headline.main}
                </h2>
                <p className="text-sm text-gray-600">{article.byline?.original || "By unknown"}</p>
              </div>
            </motion.article>
          ))}
        </div>
      </section>
    </div>
  );
};

export default ArticleGrid;
