// FullTextArticle.js
import React from "react";

const FullTextArticle = ({ article }) => {
  if (!article) return null;

  return (
    <div className="article full-text">
      <h2 className="article-title">{article.title}</h2>
      <p>{article.abstract}</p>
    </div>
  );
};

export default FullTextArticle;
