import React from "react";
import "../pages/homepage.css"; // Stile specifico per il componente

const TextArticle = ({ articlesLoad }) => {
  // Controllo per verificare che l'elenco degli articoli sia valido e non vuoto
  if (!articlesLoad || articlesLoad.length === 0) {
    return null; // Ritorna null se non ci sono articoli
  }

  return (
    <div className="article-title">
      {articlesLoad.map((article, index) => (
        <div className="text-article" key={index}>
          {article.title}
        </div>
      ))}
    </div>
  );
};

export default TextArticle;
