import React from "react";
import { FaArrowRight } from "react-icons/fa";

const ArticleSection = () => {
  const articles = [
    {
      id: 1,
      title: "The Art of Modern Architecture",
      image: "images.unsplash.com/photo-1488972685288-c3fd157d7c7a",
      description: "Exploring contemporary architectural designs and their impact on urban landscapes."
    },
    {
      id: 2,
      title: "Sustainable Living Practices",
      image: "images.unsplash.com/photo-1518156677180-95a2893f3e9f",
      description: "Discover eco-friendly lifestyle choices for a better tomorrow."
    },
    {
      id: 3,
      title: "Digital Innovation Trends",
      image: "images.unsplash.com/photo-1519389950473-47ba0277781c",
      description: "Latest developments in technology shaping our future."
    },
    {
      id: 4,
      title: "Culinary Adventures",
      image: "images.unsplash.com/photo-1504674900247-0877df9cc836",
      description: "Exploring global cuisines and cooking techniques."
    },
    {
      id: 5,
      title: "Mindful Living",
      image: "images.unsplash.com/photo-1506126613408-eca07ce68773",
      description: "Practices for mental well-being and personal growth."
    },
    {
      id: 6,
      title: "Travel Destinations 2024",
      image: "images.unsplash.com/photo-1488085061387-422e29b40080",
      description: "Must-visit places around the world this year."
    },
    {
      id: 7,
      title: "Fitness Revolution",
      image: "images.unsplash.com/photo-1517836357463-d25dfeac3438",
      description: "New approaches to health and wellness."
    },
    {
      id: 8,
      title: "Creative Photography",
      image: "images.unsplash.com/photo-1516035069371-29a1b244cc32",
      description: "Tips and techniques for capturing perfect moments."
    },
    {
      id: 9,
      title: "Business Innovation",
      image: "images.unsplash.com/photo-1460925895917-afdab827c52f",
      description: "Emerging trends in entrepreneurship and leadership."
    },
    {
      id: 10,
      title: "Green Technology",
      image: "images.unsplash.com/photo-1497435334941-8c899ee9e8e9",
      description: "Environmental solutions for a sustainable future."
    }
  ];

  return (
    <div className="max-w-[1200px] mx-auto px-4 py-8">
      <section className="space-y-8">
        <h2 className="text-md font-bold uppercase text-gray-800 mb-6">Featured Articles</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-4">
          {articles.map((article) => (
            <article
              key={article.id}
              className="w-full max-w-[250px] mx-auto bg-white overflow-hidden shadow-md hover:shadow-xl transition-shadow duration-300 cursor-pointer group"
              onClick={() => console.log(`Navigating to article ${article.id}`)}
            >
              <div className="relative h-40 overflow-hidden">
                <img
                  src={`https://${article.image}`}
                  alt={article.title}
                  className="w-full h-full object-cover transform group-hover:scale-105 transition-transform duration-300"
                  onError={(e) => {
                    e.target.src = "https://images.unsplash.com/photo-1516321497487-e288fb19713f";
                    e.target.alt = "Fallback image";
                  }}
                />
              </div>
              <div className="p-4">
                <h3 className="text-lg font-semibold text-gray-800 mb-2 line-clamp-2">
                  {article.title}
                </h3>
                <p className="text-sm text-gray-600 mb-3 line-clamp-2">
                  {article.description}
                </p>
                <div className="flex items-center text-blue-600 text-sm font-medium group-hover:text-blue-800">
                  Read More
                  <FaArrowRight className="ml-2 group-hover:translate-x-1 transition-transform duration-200" />
                </div>
              </div>
            </article>
          ))}
        </div>
      </section>
    </div>
  );
};

export default ArticleSection;
