import React from "react";
import { useGlobalContext } from "../context";
import { Link } from "react-router-dom";

const RightSideArticle = ({
  section,
  published_date,
  title,
  byline,
  multimedia,
  abstract,
  url,
}) => {
  const { formatSection } = useGlobalContext();

  // Funzione per formattare la data
  const formatDate = (date) => {
    if (!date) return "Unknown Date";
    const options = { year: "numeric", month: "long", day: "numeric" };
    return new Date(date).toLocaleDateString("en-US", options);
  };

  return (
    <div className="flex w-full p-4 border-b-2 border-gray-300">
      {/* Testo (Sezione, Titolo, Abstract e Data) */}
      <div className="w-2/3 pr-4">
        {/* Sezione */}
        {section && (
          <p className="text-xs font-bold mt-2 bg-white text-black p-1 uppercase">
            {formatSection(section)}
          </p>
        )}

        {/* Titolo */}
        <h2 className="font-extrabold text-lg mt-4 text-left">
          <Link to={url} target="_blank" rel="noreferrer">
            {title}
          </Link>
        </h2>

        {/* Abstract */}
        {abstract && (
          <div className="mt-4 text-justify">
            <p className="text-sm">{abstract}</p>
          </div>
        )}

        {/* Data */}
        {published_date && (
          <div className="flex flex-row justify-between items-center mt-2">
            <p className="font-bold text-xs">{formatDate(published_date)}</p>
          </div>
        )}
      </div>

      {/* Immagine a destra */}
      {multimedia && multimedia[0] && (
        <div className="w-1/3 pl-4 flex justify-center">
          <img
            className="w-full h-32 object-cover shadow-md"
            src={multimedia[0].url}
            alt={title}
          />
        </div>
      )}
    </div>
  );
};

export default RightSideArticle;
