import React from "react";
import ArticleGrid from "./ArticleGrid";
import { BounceLoader } from "react-spinners";
import { useFetchData } from "../Hooks/useFetchData";
import "../components/SectionPageStyle.css";

const SectionBottom = () => {
  const urlSection = `https://api.nytimes.com/svc/topstories/v2/well.json?api-key=30mS33uUBWOVAYffuOfb9rpPZhDzpDjS`;

  const { load, articles } = useFetchData(urlSection);

  const articlesLoad = articles.results;

  if (load) {
    return (
      <div className="section-container mt-6 p-4">
        {/* Titolo della sezione */}
        <h2 className="text-lg font-bold text-gray-800 mb-6">WELL</h2>
        <div className="grid grid-cols-1 md:grid-cols-[2fr_1fr] gap-6">
          {/* Colonna sinistra */}
          <div className="left-column flex-column space-y-6">
          <div className="flex-1 space-y-4">
                {articlesLoad.slice(1, 4).map((article, index) => (
                  <div key={index} className="bg-white p-3">
                    <h3 className="text-lg font-bold">
                      <a
                        href={article.url}
                        target="_blank"
                        rel="noreferrer"
                        className="hover:text-blue-600"
                      >
                        {article.title}
                      </a>
                    </h3>
                    <p className="text-sm text-gray-600 mt-2">{article.abstract}</p>
                  </div>
                ))}
              </div>
            <div className="flex flex-col md:flex-row gap-6 bg-white p-4">
              {/* Immagine dell'articolo in grande */}
              <div className="flex-1 md:w-1/3">
                <img
                  src={articlesLoad[0]?.multimedia?.[0]?.url || "/placeholder.jpg"}
                  alt={articlesLoad[0]?.title}
                  className="h-auto "
                />
              </div>
            </div>
          </div>

          {/* Colonna destra */}
          <div className="right-column space-y-4">
            {articlesLoad.slice(4, 10).map((article, index) => (
              <div
                key={index}
                className="bg-white p-3"
              >
                <h4 className="text-sm font-bold">
                  <a
                    href={article.url}
                    target="_blank"
                    rel="noreferrer"
                    className="hover:text-blue-600"
                  >
                    {article.title}
                  </a>
                </h4>
                <p className="text-xs text-gray-500 mt-1">
                  {article.byline || "Author Unknown"}
                </p>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  } else {
    return (
      <BounceLoader
        className="mx-auto my-36"
        color="gray"
        size={180}
        aria-label="Loading Spinner"
      />
    );
  }
};

export default SectionBottom;
