import React from "react";
import { useFetchData } from "../Hooks/useFetchData";
import "../pages/homepage.css";

const Homepage = () => {
  const urlHome = `https://api.nytimes.com/svc/topstories/v2/home.json?api-key=30mS33uUBWOVAYffuOfb9rpPZhDzpDjS`;
  const { load, articles } = useFetchData(urlHome);

  const articlesLoad = articles.results;

  if (load) {
    return (
      <div className="homepage-container">
        {/* Colonna sinistra */}
        <div className="left-column">
          {/* Primo articolo con immagine a destra */}
          {articlesLoad.length > 0 && (
            <div className="article">
              <div className="text-content">
              <h2 className="article-title">{articlesLoad[0].title}</h2> {/* Classe aggiunta */}
              <p>{articlesLoad[0].abstract}</p>
              </div>
              <img
                src={articlesLoad[0].multimedia[0]?.url || ""}
                alt={articlesLoad[0].title}
              />
            </div>
          )}

          {/* quattro articoli */}
          <div className="grid-four-titles">
            {articlesLoad.slice(1, 5).map((article, index) => (
              <div className="article-title-only" key={index}>
                {article.title}
                </div>
            ))}
          </div>

          {/* Articolo con immagine a sinistra e metÃ  testo */}
          {articlesLoad.length > 3 && (
            <div className="article half-width">
              <img
                src={articlesLoad[4].multimedia[0]?.url || ""}
                alt={articlesLoad[4].title}
              />
              <div className="text-content">
                <h2 className="article-title">{articlesLoad[4].title}</h2>
                <p>{articlesLoad[4].abstract}</p>
              </div>
            </div>
          )}

          {/* Articolo con solo testo */}
          {articlesLoad.length > 4 && (
            <div className="article full-text">
              <h2>{articlesLoad[4].title}</h2>
              <p>{articlesLoad[4].abstract}</p>
            </div>
          )}
        </div>
      </div>
    );
  } else {
    return <div>Loading...</div>;
  }
};

export default Homepage;
