import { useState } from "react";
import { FaUser } from "react-icons/fa";

const ArticleImageRight = () => {
  const [isHovered, setIsHovered] = useState(false);

  const articleData = {
    title: "The Future of Artificial Intelligence in Modern Healthcare",
    content: "Artificial Intelligence is revolutionizing healthcare delivery and patient care. From diagnostic assistance to personalized treatment plans, AI is making significant strides in improving medical outcomes. Recent studies have shown that AI-powered systems can detect diseases earlier and with greater accuracy than traditional methods. This technological advancement is not just changing how healthcare is delivered but is also making it more accessible to populations worldwide.",
    author: "Dr. Sarah Johnson",
    image: "images.unsplash.com/photo-1576091160399-112ba8d25d1d?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2340&q=80",
    altText: "Advanced medical technology showing AI integration in healthcare"
  };

  return (
    <div className="max-w-5xl mx-auto p-6">
      <article className="bg-white overflow-hidden transition-transform duration-300 hover:shadow-xl">
        <div className="flex flex-col md:flex-row p-6 gap-8">
          <div className="flex-1 space-y-4">
            <h2 
              className={`text-2xl font-bold text-gray-800 transition-colors duration-300 ${isHovered ? "text-blue-600" : ""}`}
              onMouseEnter={() => setIsHovered(true)}
              onMouseLeave={() => setIsHovered(false)}
            >
              {articleData.title}
            </h2>
            <p className="text-gray-600 leading-relaxed">
              {articleData.content}
            </p>
          </div>
          
          <div className="md:w-[340px] space-y-4">
            <div className="relative group overflow-hidden">
              <img
                src={`https://${articleData.image}`}
                alt={articleData.altText}
                className="w-full h-[250px] object-cover transition-transform duration-300 group-hover:scale-105"
                onError={(e) => {
                  e.target.src = "https://images.unsplash.com/photo-1618005182384-a83a8bd57fbe?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1464&q=80";
                }}
              />
              <div className="absolute inset-0 bg-gradient-to-t from-black/50 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
            </div>
            
            <div className="flex items-right space-x-3 text-gray-600">
              <span className="font-small">{articleData.author}</span>
            </div>
          </div>
        </div>
      </article>
    </div>
  );
};

export default ArticleImageRight;
